    mixin PaymentSection
      section.flex.flex-col.items-center.text-18.text-center(class="desktop:py-32")&attributes(attributes)
        i18n.text-24(path="CivicPageV2.Payment.Pricing")
          span.text-gray-4a.font-400(place="price" style="font-size:62px") {{ price }}
        i18n.mx-16(
          path="CivicPageV2.Payment.CTA"
          style="max-width:510px"
        )
          i18n.text-like-green.font-400(path="civicLiker" place="civicLiker")

        .flex.justify-center.items-center.flex-wrap.mx-16.my-24(class="desktop:mt-48")
          .civic-liker-page-v2__payment-select.m-8
            slot(name="payment-select")
          i18n.civic-liker-page-v2__join-button(
            class="border-solid border-like-cyan border-2 rounded-full text-like-green font-400 m-8 p-8 cursor-pointer"
            path="CivicPageV2.Payment.JoinButtonText"
            @click="$emit('click-join')"
          )
    
    +PaymentSection

    section
      .bg-white(class="desktop:mx-24 laptop:rounded-8")
        .text-center.text-30.text-like-green.px-16.py-32
          | {{ $t('CivicPageV2.MediaSupport.title') }}
        ul.civic-liker-page-v2__media-list.list-reset.pb-40(class="laptop:px-32")
          li(
            v-for="media in mediaList"
            :key="media.id"
          )
            a(:href="media.link" target="_blank")
              img(:src="media.image")

    section.font-200.text-18
      .px-16.py-32(class="desktop:mx-48")
        .text-like-green.text-30.mx-20.my-16
          | {{ $t('CivicPageV2.MediaSupport.Stats.TotalCreators') }}
        .mx-20.my-16 {{ $t('CivicPageV2.MediaSupport.Stats.TotalRewards') }}
        .mx-20.my-16
          span.text-56 1,165,000
          span.ml-16 HKD
        .flex.flex-wrap
          .mx-20.my-4
            .text-48 700+
            div {{ $t("CivicPageV2.MediaSupport.Stats.Website") }}
          .mx-20.my-4
            .text-48 5,000+
            div {{ $t("CivicPageV2.MediaSupport.Stats.Creator") }}
          .mx-20.my-4
            .text-48 150,000+
            div {{ $t("CivicPageV2.MediaSupport.Stats.Content") }}

    section
      div(class="tablet:bg-white laptop:bg-white desktop:mx-24 laptop:rounded-8")
        .text-center.text-30.text-like-green.mx-16.py-32(
          class="border-t border-gray-9b tablet:border-t-0 laptop:border-t-0"
        )
          | {{ $t('CivicPageV2.Benefit.title') }}
        ul.civic-liker-page-v2__benefit-list.list-reset.pb-40(
          class="laptop:px-32 hidden tablet:block laptop:block"
        )
          li.civic-liker-page-v2__benefit-list-item(
            v-for="benefit in benefitList"
            :key="benefit.id"
          )
            img(:src="benefit.image")
            .text-center {{ $t(`CivicPageV2.Benefit.${benefit.id}`) }}

        div(
          class="tablet:hidden laptop:hidden"
          v-swiper:benefitSwiper="$options.benefitSwiper"
        )
          ul.civic-liker-page-v2__mobile-benefit-list(
